  //   // axios.get(`https://console-mock.apipost.cn/app/mock/project/329a5d1d-9d21-4891-dc67-c8f476a5658d/getTagList`)
  //   //   .then(response => {
  //   //     console.log(response.datad)
  //   //   })
  // })

  const Tags = () => {
    return tagData.map((tag, key) => {
      return (
        <TouchableOpacity key={key} onPress={() => {
          setCurrentTag(tag);
          onChangeTag(tag);}
        }>
          <View >
            <Text style={tag == currentTag ? styles.tag_active : styles.tag_normal}> {tag}</Text>    
          </View>
        </TouchableOpacity>
      )
    })
  }

  return (
      <View style={styles.container}>
        {Tags()}
      </View>
    )
}

// styles
const styles = StyleSheet.create({
  container: {
    marginBottom: 20,  
    justifyContent: "flex-start",
    alignItems: "center",
    flexDirection: "row",
    borderRadius: 12,
  },
  tag_active: {
    backgroundColor: '#FFFFFF',
    color: '#000000',
    fontWeight: 600,
    borderRadius: 14,
    paddingLeft: 17,
    paddingRight: 17,
    paddingTop: 5,
    paddingBottom: 5,
    marginRight: 6,
  },
  tag_normal: {
    backgroundColor: 'rgba(255,255,255,0.1)',
    color: '#FFFFFF',
    fontWeight: 500,
    borderRadius: 14,
    paddingLeft: 17,
