import React, { Component } from 'react';
import { LinearGradient } from 'expo-linear-gradient';
import { View, StyleSheet, Animated, Image, Text } from 'react-native'

export default function Message() {
    return (
        <View style={styles.container}>
            <LinearGradient colors={ ['#FF9457', '#E96114']}>
                <Image style={styles.photo} source={require('E:/Upwork/React Native/AwesomeProject/assets/Rectangle 5352.png')}></Image>
            </LinearGradient>
        </View>
    )
}

const styles = StyleSheet.create({
    
    container: {
        borderRadius: 12,
        color: Gradi
    },
    
    photo: {
        width: 24,
        height: 24,
        radius: 22,
    }

})
