import React, { Component } from 'react';
import { SafeAreaView, StyleSheet, TextInput } from "react-native";

export default function MessageForm({navigation}) {

    // const [online, setOnline] = useState(true);
    const [text, onChangeText] = React.useState("Useless Text");
    const [number, onChangeNumber] = React.useState(null);

    return (
        <SafeAreaView>
            <TextInput
                // style={styles.input}
                onChangeText={onChangeText}
                value={text}
            />
            <TextInput
                // style={styles.input}
                onChangeText={onChangeNumber}
                value={number}
                placeholder="useless placeholder"
                keyboardType="numeric"
            />
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        borderRadius: 30,
        padding: 21,
        paddingTop: 0,
        // Left: 21,
        backgroundColor: '#212027'
    },

    border: {
        width: 28.5,
        borderBottomWidth: 3,
        borderColor: '#FFFFFF',
        alignSelf: 'center',
        marginTop: 12,
        borderRadius: 1
    },

    button: {
        backgroundColor: '#FF7C24',
        borderRadius: 12,
        paddingTop: 18,
        paddingBottom: 18,
        textAlign: 'center',
        fontSize: 18,
        color: '#FFFFFF'
    },

    label: {
        fontSize: 16,
        lineHeight: 21,
        marginTop: 24,
        marginBottom: 11,
        alignItems: 'center',
        color: '#FFFFFF'
    },

    input: {
        backgroundColor: 'rgba(255,255,255,0.1)',
        borderRadius: 12,
        paddingLeft: 21,
        paddingRight: 21,
        flexDirection: 'row',
        justifyContent: 'space-between',
        height: 54,
        alignItems: 'center',
        // color: '#FFFFFF'
    },

    category_active: {
        backgroundColor: '#FFFFFF',
        color: '#000000',
        fontWeight: 600,
        borderRadius: 14,
        paddingLeft: 17,
        paddingRight: 17,
        paddingTop: 5,
        paddingBottom: 5,
        marginRight: 6,
      },
      category_normal: {
        backgroundColor: 'rgba(255,255,255,0.1)',
        color: '#FFFFFF',
        fontWeight: 500,
        borderRadius: 14,
        paddingLeft: 17,
        paddingRight: 17,
        paddingTop: 5,
        paddingBottom: 5,
        marginRight: 6,
      }

})
