import React, { Component, useState, useEffect } from 'react';
import { View, StyleSheet, Animated, Image, Text} from 'react-native'
import { useRoute } from '@react-navigation/native';

export default function PostContent({navigation, fadeOut}) {

    const route = useRoute();
    const [offset, setOffset] = useState(new Animated.Value(-21))

    useEffect(() => {
        Animated.timing(offset, { toValue: 0, duration: 1500 }).start();
    })

    return (

        <View style={{
            padding: 21,
        }}>
            <Animated.View
                style={{
                    transform: [
                        { translateY: offset },
                        { translateX: offset }
                    ]
            }}>
                <Text style={{
                paddingRight: 21,
                paddingLeft: 21,
                paddingTop: 56,
                paddingBottom: 56,
                marginBottom: 27,
                borderRadius: 12,
                border: 'border: 1px solid rgba(255, 255, 255, 0.19)',
                fontWeight: 600,
                fontSize: 22,
                lineHeight: 30,
                color: '#FFFFFF',
                textAlign: 'center',
                }}>
                {route.params.content}
                </Text>
            </Animated.View>    
        </View>
    )
}

const styles = StyleSheet.create({

})
