import React, { Component } from 'react';
import { SafeAreaView, StyleSheet, TextInput, Text, TouchableOpacity, View } from "react-native";
import { ScreenStackHeaderRightView } from 'react-native-screens';
import { Feather, Entypo } from '@expo/vector-icons';

export default function MessageForm({navigation}) {

    // const [online, setOnline] = useState(true);
    const [text, onChangeText] = React.useState("");
    const [number, onChangeNumber] = React.useState(null);

    // Navigate to Broadcast Form
    const toBroadCast = () => {
        navigation.navigate('broadcast', {
            
        })
    }

    return (
        <View style={styles.container}>
            <TextInput
                style={styles.input}
                onChangeText={onChangeText}
                value={text}
                multiline={true}
                placeholder="Wanna talk about..."
            />
            <View style={{ flexDirection: 'row', justifyContent: 'flex-end' }}>
                <TouchableOpacity onPress={toBroadCast}>
                    <Text style={styles.button}>
                    <Feather
                        name="arrow-right"
                        size={20}
                        color="white"
                        style={{ marginLeft: 1 }}
                    />
                    </Text>
                </TouchableOpacity>
            </View>
        
        </View >
    )
}

const styles = StyleSheet.create({
    container: {
        borderRadius: 30,
        padding: 21,
        // Left: 21,
        backgroundColor: '#212027'
    },

    input: {
        color: '#fff',
        fontWeight: 400,
        fontSize: 22,
        lineHeight: 30,
        paddingLeft: 14,
        paddingRight: 14,
        marginBottom: 20,
        height: 300,
    },  

    button: {
        width: 64,
        height: 64,
        borderRadius: 32,
        backgroundColor: '#FF7C24',
        color: '#FFFFFF',
        float: 'right',
        textAlign: 'center',
        alignItems: 'center',
        fontSize: 32,
    }

})
