import React, { Component, useState, useEffect } from 'react';
import { View, StyleSheet, Animated, Image, Text } from 'react-native'
import { useRoute } from '@react-navigation/native';

export default function PostContent({navigation, fadeOut}) {

    const route = useRoute();
    const [fadeAnimation, setFadeAnim] = useState(new Animated.Value(1))

    useEffect(() => {
        if (fadeOut == true) {
            doFadeAnimation()
        }
    }, [fadeOut])


    return (

            <View style={{padding: 21}}>
                <Text style={{
                    background: 'linear-gradient(111.39deg, #FF9457 -7.95%, #E96114 64.07%)',
                    paddingRight: 21,
                    paddingLeft: 21,
                    paddingTop: 56,
                    paddingBottom: 56,
                    marginBottom: 27,
                    borderRadius: 12,
                    border: 'border: 1px solid rgba(255, 255, 255, 0.19)',
                    fontWeight: 600,
                    fontSize: 22,
                    lineHeight: 30,
                    color: '#FFFFFF',
                    textAlign: 'center',
                    }}>
                        sadsad
                    {/* {route.params.content} */}
                </Text>
            </View>
    )
}

const styles = StyleSheet.create({

})
