import React, { Component, useEffect, useState } from 'react';
import { View, StyleSheet, Animated, Image, Text } from 'react-native';

export default function Post({backColor, message}) {
    
    useEffect(() => {
        Animated.spring(new Animated.Value(-100), { toValue: 0 }).start();
    })



    const postList = () => {
        return (
            <Animated.View style={{ transform: [{translateX: new Animated.Value(-100 + 2 * 20)}] }}>
            <View style={{
            // background: "linear-gradient(#FF9457, #E96114)",
            background: backColor,
            borderRadius: 12,
            padding: 20,
            paddingTop: 0,
            marginBottom: 14,
            color: '#FFFFFF'
        }}>
            <View style={styles.footer}>
                <View style={{ flexDirection: 'row', justifyContent:'flex-start' }}>
                    <Image style={styles.photo} source = {{uri: message.profile_img}}></Image>
                    <Text style={styles.username}>{message.sender_name}</Text>
                </View>

                <View style={{ flexDirection: 'row', justifyContent:'flex-end' }}>
                    <Text style={styles.category}>{message.tag}</Text>
                </View>
            </View>

            <View style={styles.footer}>
                <Text style={styles.content}>{message.content}</Text>
            </View>

            <View style={styles.footer}>
                <View style={{ flexDirection: 'row', justifyContent:'flex-start' }}>
                    <Image style={styles.icon} source={require('E:/Upwork/React Native/AwesomeProject/assets/Vector.png')}></Image>
                    <Image style={styles.icon} source={require('E:/Upwork/React Native/AwesomeProject/assets/Combined-Shape.png')}></Image>
                    <Image style={styles.icon} source={require('E:/Upwork/React Native/AwesomeProject/assets/send.png')}></Image>
                </View>

                <View style={{ flexDirection: 'row', justifyContent:'flex-end' }}>
                    <Text style={styles.text}>{message.comments} Comments</Text>
                    <Image style={styles.dot} source={require('E:/Upwork/React Native/AwesomeProject/assets/Ellipse.png')}></Image>
                    <Text style={styles.text}>{message.likes} likes</Text>
                </View>
            </View>

            </View>
        </Animated.View>
        )
    }
}

const styles = StyleSheet.create({
    
    photo: {
        width: 24,
        height: 24,
        borderRadius: 22
    },

    username: {
        fontWeight: 500,
        fontSize: 14,
        lineHeight: 18,
        marginLeft: 10,
        color: '#FFFFFF'
    },

    category: {
        height: 22,
        backgroundColor: 'rgba(255,255,255,0.2)',
        borderColor: '#FFFFFF',
        color: '#FFFFFF',
        borderWidth: 1,
        borderRadius: 12,
        fontWeight: 500,
        fontSize: 14,
        paddingLeft: 8,
        paddingRight: 8,
        paddingBottom:2,
    },

    content: {
        fontWeight: 600,
        fontSize: 17,
        lineHeight: 22.1,
        color: '#FFFFFF'
    },

    icon: {
        width: 17,
        height: 17,
        marginRight: 18.3,
    },

    footer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingTop: 20,
    },

    text: {
        fontWeight: 500,
        fontSize: 12,
        color: '#FFFFFF'
    },

    dot: {
        marginLeft: 6,
        marginRight: 6,
        width: 3,
        height: 3,
        marginTop: 7
    }

})
