import React, { FunctionComponent, useEffect, useState } from 'react';
import { View, ScrollView, StyleSheet, Animated, Image, Text, TouchableOpacity } from 'react-native'
import axios from 'axios';
import { useFonts } from 'expo-font';
import { BASE_URL } from '../../Constant'
import { useRoute } from '@react-navigation/native';
import { Feather, Entypo } from '@expo/vector-icons';
import Profile from "./profile";
import Search from "./search";
import Tag from '../../Components/TagList/tagList';
import PostList from "./postList";
import PostForm from "../Post"
import { Routes } from 'react-router-native';

const Dashboard: FunctionComponent = ({ navigation }) => {
    // Load Font
    const [loaded] = useFonts({
        IBMPlex: require('../../../assets/fonts/IBMPlexSans-Text.ttf'),
    });
    
    const route = useRoute()

    const [postList, setPostListData] = useState(
                        [{ "post_id": "6Cdfd3A3-a862-4bfc-5E6E-2a6B42C89828", "profile_img": "https://i.pravatar.cc/150?u=17090", "createdAt": "1982-10-05 23:46:23", "sender_name": "Deborah Martin", "comments": 10643, "likes": 3422, "content": "要光使示。", "tag": "奥利给" },
                        { "post_id": "cc9BF43F-efea-c84e-f69e-dDFFA3cCbaB5", "profile_img": "https://i.pravatar.cc/150?u=32618", "createdAt": "1976-05-11 09:19:31", "sender_name": "Anna White", "comments": 3650, "likes": 15341, "content": "干构也产火通反日称先布收。", "tag": "computer" },
                        { "post_id": "BaBcf9Be-bfB2-71E1-E65a-BAb22228115E", "profile_img": "https://i.pravatar.cc/150?u=96080", "createdAt": "1996-05-02 06:38:08", "sender_name": "Matthew White", "comments": 9500, "likes": 19059, "content": "全段争无同少江片往了员是美龙上难为。", "tag": "computer" },
                        { "post_id": "eDDBC672-dFdb-F6A9-4766-A7bff0709bf8", "profile_img": "https://i.pravatar.cc/150?u=84904", "createdAt": "1999-03-11 09:27:04", "sender_name": "Frank Allen", "comments": 13238, "likes": 3414, "content": "法动道明满相例严较半相变认。", "tag": "travel" },
                        { "post_id": "b1dEfF29-3B63-b692-64D4-310581da82BF", "profile_img": "https://i.pravatar.cc/150?u=56785", "createdAt": "1987-08-16 19:29:26", "sender_name": "Eric Robinson", "comments": 11022, "likes": 14253, "content": "位好白书员该约公在理低万论片速文儿组子。", "tag": "computer" }
        ]);
    const [filteredPostList, setFilterPostList] = useState(postList);
    const [keyword, setKeyword] = useState('');
    const [tag, setTag] = useState('all');
    const [offset, setOffset] = useState(new Animated.Value(250)) // Animation
    
    // Get Post Data through API
    const getPostList = async() => {
        const { data } = await axios.get(BASE_URL + '/getAllPost')
        setPostListData(data)
        setFilterPostList(data)
    }

    useEffect(() => {
        // getPostList()
    }, [route])

    // When user inputs Search keyword
    const onChangeKeyword = (key) => {
        setKeyword(key)
        onFileterPostList(key, tag)
    }

    // When user selects tag
    const onChangeTag = (cate) => {
        setTag(cate)
        onFileterPostList(keyword, cate)
    }

    // To filter post messages
    const onFileterPostList = (key, cate) => {
        setFilterPostList(
            postList.filter(msg => {
                const flag = cate == 'all' ? true : msg.tag == cate
                // console.log(category)
                console.log(flag)
                return (msg.sender_name.includes(key) || msg.content.includes(key)) && flag;
            })
        )
    }

    const isModal = route.params && route.params.is_modal;
    console.log(isModal)
    const [page, setPage] = useState(-1);

    useEffect(() => {
        console.log(route, 'here')
        Animated.timing(offset, { toValue: 0, duration: 2000 }).start();
        if (!(route.params && route.params.is_modal))
            setPage(3);
        else setPage(0)
    }, [route]);

    const mainPage = () => {
        return <View style={{ flex: 1 }}>
            
        <ScrollView style={{ backgroundColor: '#0D0C13', padding: 21, fontFamily: 'IBMPlex' }}>
            <Search onChangeKeyword={onChangeKeyword} />
            <Tag onChangeTag={onChangeTag} onlyInterested={true} onlyAnimated={true} />
            <PostList messages={filteredPostList} />
        </ScrollView>
        
        <Animated.View style={{ transform: [{translateY: offset}] }}>
            <TouchableOpacity onPress={() => { navigation.navigate('editor') }} style={styles.footer}>
                <Text style={styles.button}>
                    <Feather
                        name="plus"
                        size={20}
                        color="white"
                    />
                </Text>
            </TouchableOpacity>
        </Animated.View>
    </View>
    }

    const displayContent = () => {
        if (page == 0) return <PostForm />
        if (page == 1) return <></>
        if (page == 3) return mainPage();
        return <View></View>
    }

    return displayContent();

}

const styles = {
    button: {
        width: 64,
        height: 64,
        zIndex: -1,
        borderRadius: 32,
        backgroundColor: '#FF7C24',
        color: '#FFFFFF',
        float: 'right',
        textAlign: 'center',
        alignSelf: 'center',
        alignItems: 'center',
        fontSize: 32,
    },
    footer: {
        position: 'absolute',
        bottom: 30,
        alignSelf: 'center'
    }
}

export default Dashboard;
