import React, { Component } from 'react';
import { View, StyleSheet, Image, Text, Button, TouchableOpacity, TextInput } from 'react-native'
import { useRoute } from '@react-navigation/native';

export default function LocationForm({ navigation }) {
    const route = useRoute();

    return (
        <View style={styles.container}>

            <Text>Pick Your Location</Text>
            <TextInput></TextInput>
            <View style={{ flexDirection: 'row', justifyContent:'flex-start' }}>
                <Image style={styles.img} source={require('E:/Upwork/React Native/AwesomeProject/assets/location.png')} />
                <Text>Current Location</Text>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        borderRadius: 30,
        // Left: 21,
        backgroundColor: '#212027'
    },

    border: {
        width: 28.5,
        borderBottomWidth: 3,
        borderColor: '#FFFFFF',
        alignSelf: 'center',
        marginTop: 12,
        borderRadius: 1
    },

    img: {
        width: 23.8,
        height: 34,
        backgroundColor: '#FF7C24'
    },

    text: {
        fontWeight: 500,
        fontSize: 20,
        
    },

})
