import React, { Component, useEffect, useState } from 'react';
import { View, StyleSheet, Animated, Image, Text, TouchableOpacity } from 'react-native'
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { BASE_URL } from '../../../Constant'
import { SET_USER_PROFILE } from '../../../Redux/Actions/action';

export default function Profile({ navigation }) {
    
    const [profile, setProfile] = useState({
        "user_id": "4c2eE6C2-EC5b-cC95-2Fc0-BBBBBBBBBEA9", "username": "Amy Lee", "profile_img": "https://i.pravatar.cc/150?u=20335", "age": 58, "tags": { "1": "sports", "2": "computer", "4": "guide", "6": "热爱", "7": "快乐", "8": "奥利给" }
    })
    
    const dispatch = useDispatch()

    const saveProfileToRedux = () => {
        dispatch({type: SET_USER_PROFILE, data: profile})
    }

    saveProfileToRedux();
    // localStorage.setItem('userinfo', profile.user_id)
    // localStorage.setItem('tags', JSON.stringify(profile.tags))
    
    const getProfile = async () => {
        const { data } = await axios.get(BASE_URL + "/getUserInfo")
        setProfile(data)
        localStorage.setItem('userinfo', data)
    }

    useEffect(() => {
        getProfile();
    })

    return (
        <View style={{ height: 200}}>
            <View style={{ flexDirection: 'row', justifyContent:'space-between' }}>
                <View style={{ flexDirection: 'row', justifyContent: 'flex-start' }}>
                    <View>
                        <Image style={styles.photo} source = {{uri: profile.profile_img}} />
                        <Image style={styles.badge} source={require('E:/Upwork/React Native/AwesomeProject/assets/badge.png')} />
                    </View>
                    <Text style={styles.username}>{profile.username}</Text>
                </View>

                <View style={{ flexDirection: 'row', justifyContent: 'flex-end' }}>
                    <TouchableOpacity>
                        <View style={styles.rect}>
                            <Image style={styles.icon} source={require('E:/Upwork/React Native/AwesomeProject/assets/email.png')} />
                        </View>
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({


    photo: {
        width: 40,
        height: 40,
        borderRadius: 22
    },
    
    badge: {
        width: 14,
        height: 14,
        position: 'absolute',
        left: 28,
        top: 26,
    },

    rect: {
        width: 40,
        height: 40,
        backgroundColor: 'rgba(255, 255, 255 , 0.1)',
        borderRadius: 22,
        padding: 10,
    },

    icon: {
        width: 20,
        height: 20,
    },

    username: {
        marginLeft: 16,
        fontSize: 20,
        fontWeight: 700,
        fontFamily: 'IBMPlex',
        alignSelf: 'center',
        color: '#F5F5F5'
    }
})
