import React, { FunctionComponent, useEffect, useState } from 'react';

import { SafeAreaView, StyleSheet, TextInput, Text, TouchableOpacity, View, Animated } from "react-native";
import { ScreenStackHeaderRightView } from 'react-native-screens';
import { Feather, Entypo } from '@expo/vector-icons';

export default function PostEditor({navigation}) {

    const [text, onChangeText] = React.useState("");
    const [offset, setOffset] = useState(new Animated.Value(-100)) // Animation

    useEffect(() => {
        Animated.timing(offset, { toValue: 0, duration: 1000 }).start();
    })

    return (
        <View style={styles.container}>
            <TextInput
                style={styles.input}
                onChangeText={onChangeText}
                value={text}
                multiline={true}
                placeholder="Wanna talk about..."
            />

            <Animated.View style={{ transform: [{translateY: offset}] }}>
                <View style={{ flexDirection: 'row', justifyContent: 'flex-end' }}>
                    
                    <Text style={styles.letterCount}>
                        {text.length}/100
                    </Text>

                    <TouchableOpacity onPress={() => navigation.navigate('post', {content: text})}>
                        <Text style={styles.button}>
                        <Feather
                            name="arrow-right"
                            size={20}
                            color="white"
                            style={{ marginLeft: 1 }}
                        />
                        </Text>
                    </TouchableOpacity>


                </View>
            </Animated.View>
        
        </View >
    )
}

const styles = StyleSheet.create({
    container: {
        borderRadius: 30,
        padding: 21,
        // Left: 21,
        backgroundColor: '#212027',
        flex: 1
    },

    input: {
        color: '#fff',
        fontWeight: 400,
        fontSize: 22,
        lineHeight: 30,
        paddingLeft: 14,
        paddingRight: 14,
        marginBottom: 20,
        selectionColor: '#FF7C24',
        caretColor: '#FF7C24',
        flex: 1,
    },  

    button: {
        width: 64,
        height: 64,
        borderRadius: 32,
        backgroundColor: '#FF7C24',
        color: '#FFFFFF',
        float: 'right',
        textAlign: 'center',
        alignItems: 'center',
        fontSize: 32,
    },

    letterCount: {
        textAlign: 'center',
        fontWeight: 500,
        fontSize: 14,
        lineHeight: 24,
        color: 'rgba(255,255,255,0.35)',
        marginRight: 22,
        marginTop: 20
    }

})
