import React, { Component } from 'react';
import { View, StyleSheet, Animated, Image, TextInput } from 'react-native';
import { Feather, Entypo } from '@expo/vector-icons';

export default function Search() {
    return (
        <View style={styles.container}>
            {/* search Icon */}
            <Feather
                name="search"
                size={20}
                color="white"
                style={{ marginLeft: 1 }}
            />
            {/* Input field */}
            
            <TextInput
                style={styles.input}
            />
        </View>
      
    )
}

// styles
const styles = StyleSheet.create({
  container: {
    margin: 15,
    justifyContent: "flex-start",
    alignItems: "center",
    flexDirection: "row",
    backgroundColor: 'rgba(255,255,255,0.1)',
        height: 54,
    
  },
  searchBar__unclicked: {
    padding: 10,
    flexDirection: "row",
    width: "95%",
    backgroundColor: "#d9dbda",
    borderRadius: 15,
    alignItems: "center",
  },
  searchBar__clicked: {
    padding: 10,
    flexDirection: "row",
    width: "80%",
    backgroundColor: "#d9dbda",
    borderRadius: 15,
    alignItems: "center",
    justifyContent: "space-evenly",
  },
  input: {
    fontSize: 20,
    marginLeft: 10,
    width: "90%",
  },
});
