import React, { Component, useState, useEffect } from 'react';
import { View, StyleSheet, Animated, Image, Text } from 'react-native'
import { useRoute } from '@react-navigation/native';

export default function PostContent({fadeIn}) {

    const route = useRoute();
    const [fadeAnimation, setFadeAnim] = useState(new Animated.Value(1))

    useEffect(() => {
        if (fadeIn == true) {
            console.log("fade")
            doFadeAnimation()
        }
    }, [fadeIn])

    const doFadeAnimation = () => {
        Animated.timing(fadeAnimation, {
            toValue: 0,
            duration: 2000,
        }).start()
    }

    return (
        <Animated.View
            style={
            [{
                opacity: fadeAnimation,
                transform: [
                    {
                        scaleX: fadeAnimation
                    },
                    {
                        scaleY: fadeAnimation
                    },
                    {
                        translateY: fadeAnimation.interpolate({
                            inputRange: [0, 1],
                            outputRange: [100, 0]
                        })
                    },
                    {
                        translateX: fadeAnimation.interpolate({
                            inputRange: [0, 1],
                            outputRange: [100, 0]
                        })
                    }
                ]
            }]
        }>
            <View style={{padding: 21}}>
            <Text style={{
                background: 'linear-gradient(111.39deg, #FF9457 -7.95%, #E96114 64.07%)',
                paddingRight: 21,
                paddingLeft: 21,
                paddingTop: 56,
                paddingBottom: 56,
                marginBottom: 27,
                borderRadius: 12,
                border: 'border: 1px solid rgba(255, 255, 255, 0.19)',
                fontWeight: 600,
                fontSize: 22,
                lineHeight: 30,
                color: '#FFFFFF',
                textAlign: 'center',
            }}>
                {route.params.content}
            </Text>
            </View>
        </Animated.View>
    )
}

const styles = StyleSheet.create({

})
