import React, { Component } from 'react';
import { View, StyleSheet, Animated, Image, TextInput, TouchableOpacity, Text, TouchableOpacityBase } from 'react-native';
import { useEffect, useState } from 'react';
import { BASE_URL } from '../../Constant'
import Tag from './Tag/tag';
import axios from 'axios';

export default function TagList({ onChangeTag, onlyInterested, onlyAnimated}) {

  const [currentTag, setCurrentTag] = useState('all');
  const [tagData, setTagData] = useState(["all", "finance","sports","computer","travel","guide","foodie","热爱","快乐","奥利给","space"])
  const getTagList = async () => {
    
    if (onlyInterested == true) { //Only load Interested Tag
      const tags = JSON.parse(localStorage.getItem('tags'))
      const data = ['all']
      for (let key in tags) {
        data.push(tags[key])
      }
      console.log(data)
      setTagData(data)
    }
    else {  // Load All Tags.
      const { data } = await axios.get(BASE_URL + "/getTagList")
      setTagData(data)
    }
  }

  const handleTag = (name) => {
    setCurrentTag(name)
  }
  
  useEffect(() => {
    getTagList()
  }, [onlyInterested])

  const Tags = () => {
    return tagData.map((tag, key) => {
      return (
        <Tag content={tag} index={key} currentTag={currentTag} onChangeTag={onChangeTag}
             setCurrentTag={handleTag} onlyAnimated={onlyAnimated} />
      )
    })
  }

  return (
      <View style={styles.container}>
        {Tags()}  
      </View>
    )
}

// styles
const styles = StyleSheet.create({
  container: {
    marginBottom: 20,  
    justifyContent: "flex-start",
    alignItems: 'stretch',
    flexDirection: 'row',
    flexWrap: 'wrap',
    // flexDirection: "row",
    borderRadius: 12,
  },
  tag_active: {
    backgroundColor: '#FFFFFF',
    color: '#000000',
    fontWeight: 600,
    borderRadius: 14,
    paddingLeft: 17,
    paddingRight: 17,
    paddingTop: 5,
    paddingBottom: 5,
    marginRight: 6,
    marginTop: 10,
  },
  tag_normal: {
    backgroundColor: 'rgba(255,255,255,0.1)',
    color: '#FFFFFF',
    fontWeight: 500,
    borderRadius: 14,
    paddingLeft: 17,
    paddingRight: 17,
    paddingTop: 5,
    paddingBottom: 5,
    marginRight: 6,
  }

});
