import React, { Component } from 'react';
import { View, StyleSheet, Image, Text, Button, TouchableOpacity, Switch, Picker, Animated } from 'react-native';
import { Actions } from 'react-native-router-flux';
import { useEffect, useState } from 'react';
import { useRoute } from '@react-navigation/native';
import Tag from '../../../Components/TagList';


export default function PostForm({navigation}) {

    const route = useRoute();

    const [online, setOnline] = useState(true);
    const [tag, setTag] = useState('all');
    const [location, setLocation] = useState('Beijing')

    // Navigate to Broadcast Form
    const toBroadCast = () => {
        navigation.navigate('broadcast', {
            user_id: localStorage.getItem('userinfo'),
            content: route.params.content,
            tags: tag,
            location: location,
            onlineOnly: online,
        })
    }
    
    // When you click tag
    const onChangeTag = (newTag: string) => {
        setTag(newTag)
    }

    return (
        <View style={styles.container}>
            <View style={styles.border}></View>
            <Text style={styles.label} >Location</Text>
            <TouchableOpacity onPress={() => {navigation.navigate('location')}}>
                <Picker style={styles.input} >
                    <Picker.Item label="Beijing" value="bj" />
                </Picker>
            </TouchableOpacity>

            <Text style={styles.label}>Status</Text>
            <View style={styles.input}>
                <Text style={{ color: 'white' }}>{online == true ? 'Online' : 'Offline'}</Text>
                <Switch
                    value={online}
                    onValueChange={() => { setOnline(!online)}}
                />
            </View>
            <Animated.View style={{ transform: [{ translateY: offset }] }}>
                <Text style={styles.label}>Interested</Text>
            </Animated.View>
            <Animated.View style={{ transform: [{ translateY: offset }] }}>
                <Tag onChangeTag={onChangeTag} onlyInterested={false} />
            </Animated.View>
            
            <Animated.View style={{ transform: [{ translateY: offset }] }}>
                <TouchableOpacity onPress={toBroadCast}>
                    <Text style={styles.button}>
                    Next
                    </Text>
                </TouchableOpacity>
            </Animated.View>

        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        borderRadius: 30,
        padding: 21,
        paddingTop: 0,
        flex: 1,
        backgroundColor: '#212027'
    },

    border: {
        width: 28.5,
        borderBottomWidth: 3,
        borderColor: '#FFFFFF',
        alignSelf: 'center',
        marginTop: 12,
        borderRadius: 1
    },

    button: {
        backgroundColor: '#FF7C24',
        borderRadius: 12,
        paddingTop: 18,
        paddingBottom: 18,
        textAlign: 'center',
        fontSize: 18,
        color: '#FFFFFF'
    },

    label: {
        fontSize: 16,
        lineHeight: 21,
        marginTop: 24,
        marginBottom: 11,
        alignItems: 'center',
        color: '#FFFFFF'
    },

    input: {
        backgroundColor: 'rgba(255,255,255,0.1)',
        borderRadius: 12,
        paddingLeft: 21,
        paddingRight: 21,
        flexDirection: 'row',
        justifyContent: 'space-between',
        height: 54,
        alignItems: 'center',
        // color: '#FFFFFF'
    },

    category_active: {
        backgroundColor: '#FFFFFF',
        color: '#000000',
        fontWeight: 600,
        borderRadius: 14,
        paddingLeft: 17,
        paddingRight: 17,
        paddingTop: 5,
        paddingBottom: 5,
        marginRight: 6,
      },
      category_normal: {
        backgroundColor: 'rgba(255,255,255,0.1)',
        color: '#FFFFFF',
        fontWeight: 500,
        borderRadius: 14,
        paddingLeft: 17,
        paddingRight: 17,
        paddingTop: 5,
        paddingBottom: 5,
        marginRight: 6,
      }

})
