import React, { Component } from 'react';
import { View, StyleSheet, Animated, Image, TextInput, TouchableOpacity, Text, TouchableOpacityBase } from 'react-native';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Category() {

  const [tab, setTab] = useState('all');
  const [tabData, setTabData] = useState(["finance","sports","computer","travel","guide","foodie","热爱","快乐","奥利给","space"])

  useEffect(() => {
    // axios.get(`https://console-mock.apipost.cn/app/mock/project/329a5d1d-9d21-4891-dc67-c8f476a5658d/getTagList`)
    //   .then(response => {
    //     console.log(response.datad)
    //   })
    
  })

    return (
      <View style={styles.container}>
        {tabData.map(tab => {
                  <TouchableOpacity onPress={() => setTab('all')}>
                  <View >
                    <Text style={tab=='all'?styles.category_active:styles.category_normal}>All</Text>    
                  </View>
              </TouchableOpacity>
        })}
        <TouchableOpacity onPress={() => setTab('all')}>
            <View >
              <Text style={tab=='all'?styles.category_active:styles.category_normal}>All</Text>    
            </View>
        </TouchableOpacity>
        
        <TouchableOpacity onPress={() => setTab('fin')}>
            <Text style={tab=='fin'?styles.category_active:styles.category_normal}>Finance</Text>    
        </TouchableOpacity>

        <TouchableOpacity onPress={() => setTab('mus')}>
            <Text style={tab=='mus'?styles.category_active:styles.category_normal}>Music</Text>    
        </TouchableOpacity>

        <TouchableOpacity onPress={() => setTab('tra')}>
            <Text style={tab=='tra'?styles.category_active:styles.category_normal}>Travel</Text>    
        </TouchableOpacity>  
      </View>
    )
}

// styles
const styles = StyleSheet.create({
  container: {
    marginBottom: 20,  
    justifyContent: "flex-start",
    alignItems: "center",
    flexDirection: "row",
    borderRadius: 12,
  },
  category_active: {
    backgroundColor: '#FFFFFF',
    color: '#000000',
    fontWeight: 600,
    borderRadius: 14,
    paddingLeft: 17,
    paddingRight: 17,
    paddingTop: 5,
    paddingBottom: 5,
    marginRight: 6,
  },
  category_normal: {
    backgroundColor: 'rgba(255,255,255,0.1)',
    color: '#FFFFFF',
    fontWeight: 500,
    borderRadius: 14,
    paddingLeft: 17,
    paddingRight: 17,
    paddingTop: 5,
    paddingBottom: 5,
    marginRight: 6,
  }

});
